package test1;

public class J02_Bianliang {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//定义变量
		int a;
		a=10;
		int a1=10;
		System.out.println("a="+a);
		System.out.println("a1="+a1);
		
		//同时定义多个变量
		int b1,b2,b3;
		b1=10;
		b2=20;
		b3=30;
		System.out.printf("b1=%d"+" b2=%d"+" b3=%d\n",b1,b2,b3);
		
		//注意:不能使用未赋值的变量
		
		//定义多个变量并初始化
		int c=10,c1=20,c2=23;
		System.out.println("c="+c+" c1="+c1+" c2="+c2);

	}

}
